function delivery_info( $delivery_id ) {
	$sql = 'select date, total, vat '
	       . ' from im_delivery where id = ' . $delivery_id;

	$row = sql_query_single( $sql );

	$line = "<td>" . $row[0] . '</td>';
	$line .= "<td>" . $row[1] . "</td>";
	$line .= "<td>" . $row[2] . "</td>";

	return $line;
}

