require_once( 'store_common.php' );

$target_dir    = "uploads/";
$target_file   = $target_dir . "prices.csv";
$uploadOk      = 1;
$imageFileType = pathinfo( $target_file, PATHINFO_EXTENSION );
if ( move_uploaded_file( $_FILES["fileToUpload"]["tmp_name"], $target_file ) ) {
	echo "The file " . basename( $_FILES["fileToUpload"]["name"] ) . " has been uploaded.";
}

// The file is on server. Lets read it
$file   = fopen( "uploads/prices.csv", "r" );
$header = fgetcsv( $file );

for ( $i = 0; $i < count( $header ); $i ++ ) {
	switch ( $header[ $i ] ) {
		case 'פריט':
			$item_code_idx = $i;
			break;
		case 'שם פריט':
		case 'שם':
			$name_idx = $i;
			break;
		case 'מחיר':
		case 'סיטונאות':
			$price_idx = $i;
			break;
	}
}
$line_number = 0;

while ( ( $data = fgetcsv( $file ) ) !== false ) {
	if ( $data ) {
		$price     = $data[ $price_idx ];
		$name      = $data[ $name_idx ];
		$item_code = $data[ $item_code_idx ];
		my_log( "price = " . $price . " name = " . $name . " item_code = " . $item_code, "catalog-upload-prices.php" );
		catalog_add_price( 2, $item_code, date( 'Y-m-d' ), $price, $name );
		$line_number ++;
	}
}
echo $line_number . " שורות נקלטו!";

